import * as THREE from '../node_modules/three/build/three.module.js';
import { GLTFLoader } from '../node_modules/three/examples/jsm/loaders/GLTFLoader.js';

let scene, camera, renderer;
const npcDialogueDiv = document.getElementById('npc-dialogue');
const popupContainer = document.getElementById('popup-container');
const popupContentDiv = document.getElementById('popup-content');

let worldModel, gymNpc, learnNpc, portfolioNpc; // NPCs models
let player; // Player character model
let playerMixer; // Animation mixer for player
let playerAnimations = {}; // Object to store player animations
let currentAnimationState = 'idle'; // Track current animation state

let moveDirection = new THREE.Vector3(); // Player movement direction
let moveSpeed = 0.1;
let clock = new THREE.Clock(); // Clock for animation updates
let cameraTargetOffset = new THREE.Vector3(0, 2, 5); // Camera offset from player

let npcs = []; // Array to hold interactive NPCs
let currentNPC = null; // Track the currently interacted NPC

// Movement flags
let moveForward = false;
let moveBackward = false;
let moveLeft = false;
let moveRight = false;


init();
animate();

function init() {
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 5, 10); // Initial camera position - adjust as needed

    renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Loaders
    const gltfLoader = new GLTFLoader();

    // 1. Load World Scene
    gltfLoader.load('./assets/models/port_world.glb', function (gltf) {
        worldModel = gltf.scene;
        scene.add(worldModel);
    }, undefined, function (error) {
        console.error("An error happened loading the world model:", error);
    });

    // 2. Load NPC Locations from locations.json and then load NPCs
    fetch('./data/locations.json')
        .then(response => response.json())
        .then(locationsData => {
            // Dynamically create npcPaths from locationsData
            const npcPaths = locationsData.map(location => ({
                name: location.npcName,
                path: `./assets/models/${location.npcName}.glb`, // Assuming model names match npcName
                position: getNpcPosition(location.npcName), // Function to get positions based on NPC name
                dataFile: location.dataFile,
                popupTitle: location.popupTitle
            }));

            // Load NPCs from npcPaths
            npcPaths.forEach(npcInfo => {
                gltfLoader.load(npcInfo.path, function (gltf) {
                    const model = gltf.scene;
                    model.position.set(npcInfo.position.x, npcInfo.position.y, npcInfo.position.z);
                    scene.add(model);

                    if (npcInfo.name === 'gymNpc') gymNpc = model;
                    if (npcInfo.name === 'learnNpc') learnNpc = model;
                    if (npcInfo.name === 'portfolioNpc') portfolioNpc = model;

                    npcs.push({ // Add all NPCs to the interactive array
                        model: model,
                        name: npcInfo.name,
                        dataFile: npcInfo.dataFile,
                        popupTitle: npcInfo.popupTitle
                    });

                }, undefined, function (error) {
                    console.error(`Error loading NPC model: ${npcInfo.name}`, error);
                });
            });

        })
        .catch(error => {
            console.error("Error loading locations.json:", error);
        });


    // 3. Load Player Character
    gltfLoader.load('./assets/models/character.glb', function (gltf) {
        player = gltf.scene;
        scene.add(player);
        player.position.set(0, 0, 0); // Initial player position

        // Animation Mixer
        playerMixer = new THREE.AnimationMixer(player);

        // Access and store animations - **REPLACE 'idle' and 'walk' with your actual animation names!**
        playerAnimations['idle'] = playerMixer.clipAction(gltf.animations.find(anim => anim.name === 'idle')); // **Find your idle animation name**
        playerAnimations['walk'] = playerMixer.clipAction(gltf.animations.find(anim => anim.name === 'walk'));   // **Find your walk animation name**

        if (playerAnimations['idle']) {
            playerAnimations['idle'].play(); // Start idle animation
            currentAnimationState = 'idle';
        }

    }, undefined, function (error) {
        console.error("Error loading player character:", error);
    });


    // Lighting
    const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.7);
    directionalLight.position.set(1, 1, 1);
    scene.add(directionalLight);


    window.addEventListener('resize', onWindowResize, false);
    document.addEventListener('click', onDocumentMouseDown, false); // For NPC interaction
    document.addEventListener('keydown', onKeyDown);
    document.addEventListener('keyup', onKeyUp);
}

function animate() {
    requestAnimationFrame(animate);

    // Update animation mixer for player
    if (playerMixer) {
        playerMixer.update(clock.getDelta());
    }

    updatePlayerMovement();
    updateCameraPosition(); // Make camera follow player

    checkNPCInteraction(); // Check for NPC interaction

    renderer.render(scene, camera);
}

function updatePlayerMovement() {
    moveDirection.set(0, 0, 0); // Reset move direction each frame

    if (moveForward) {
        moveDirection.z -= 1;
    }
    if (moveBackward) {
        moveDirection.z += 1;
    }
    if (moveLeft) {
        moveDirection.x -= 1;
    }
    if (moveRight) {
        moveDirection.x += 1;
    }

    moveDirection.normalize(); // Normalize to prevent faster diagonal movement

    if (moveDirection.lengthSq() > 0) {
        player.translateZ(moveSpeed * moveDirection.z);
        player.translateX(moveSpeed * moveDirection.x);

        if (currentAnimationState !== 'walk' && playerAnimations['walk']) {
            if (playerAnimations['idle']) playerAnimations['idle'].stop();
            playerAnimations['walk'].reset().play();
            currentAnimationState = 'walk';
        }
    } else {
        if (currentAnimationState !== 'idle' && playerAnimations['idle']) {
            if (playerAnimations['walk']) playerAnimations['walk'].stop();
            playerAnimations['idle'].reset().play();
            currentAnimationState = 'idle';
        }
    }
}


function updateCameraPosition() {
    if (!player) return; // Ensure player is loaded

    // Camera follow with offset
    camera.position.copy(player.position).add(cameraTargetOffset);
    camera.lookAt(player.position);
}


function onWindowResize() {
    camera.aspect = window.innerWidth / window.innerHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(window.innerWidth, window.innerHeight);
}


function checkNPCInteraction() {
    // 1. Raycasting setup
    const raycaster = new THREE.Raycaster();
    const mouse = new THREE.Vector2(); // Normalized mouse coordinates (not used for ray origin here)

    // Raycast from the camera position in the camera's direction
    raycaster.setFromCamera(mouse, camera); // Or consider raycasting from player's position slightly forward if desired.

    // 2. Raycast to find intersections with NPCs
    const intersects = raycaster.intersectObjects(npcs.map(npc => npc.model), true); // Recursive check

    if (intersects.length > 0) {
        const firstIntersectedObject = intersects[0].object;
        const intersectedNPC = npcs.find(npcData => firstIntersectedObject.isDescendantOf(npcData.model));

        if (intersectedNPC && intersectedNPC !== currentNPC) {
            // New NPC in view
            currentNPC = intersectedNPC;
            showNPCDialogue(`Hello! Welcome to the ${intersectedNPC.popupTitle} area. Do you want to enter?`, intersectedNPC);
        }
    } else {
        if (currentNPC) {
            // No NPC in view anymore, hide dialogue
            hideNPCDialogue();
            currentNPC = null;
        }
    }
}


function showNPCDialogue(message, npc) {
    npcDialogueDiv.textContent = message;
    npcDialogueDiv.style.display = 'block';
    npcDialogueDiv.dataset.npcName = npc.name;
}

function hideNPCDialogue() {
    npcDialogueDiv.style.display = 'none';
    npcDialogueDiv.dataset.npcName = '';
}


function onDocumentMouseDown(event) {
    if (currentNPC) {
        // NPC was interacted with, show popup
        showPopup(currentNPC.dataFile, currentNPC.popupTitle);
    }
}


function showPopup(dataFile, popupTitle) {
    popupContainer.style.display = 'flex';
    popupContentDiv.innerHTML = `<h2>${popupTitle}</h2><p>Loading data...</p>`;

    fetch(dataFile)
        .then(response => response.json())
        .then(data => {
            let contentHTML = `<h2>${popupTitle}</h2><ul>`;
            if (popupTitle === 'Skills') {
                contentHTML += "<h3>Programming Languages:</h3><ul>";
                data.programmingLanguages.forEach(skill => contentHTML += `<li>${skill}</li>`);
                contentHTML += "</ul><h3>Currently Learning:</h3><ul>";
                data.currentlyLearning.forEach(skill => contentHTML += `<li>${skill}</li>`);
                contentHTML += "</ul>";

            } else if (popupTitle === 'Projects') {
                data.projects.forEach(project => {
                    contentHTML += `<li><strong>${project.title}</strong> - ${project.description} (Technologies: ${project.technologies.join(', ')})</li>`;
                });
            } else if (popupTitle === 'Resume') {
                contentHTML += "<h3>Teaching History:</h3><ul>";
                data.teachingHistory.forEach(item => contentHTML += `<li>${item.role} at ${item.institution} - ${item.description}</li>`);
                contentHTML += "</ul>";
            }

            contentHTML += `</ul>`;
            popupContentDiv.innerHTML = contentHTML;

        })
        .catch(error => {
            console.error("Error loading data:", error);
            popupContentDiv.innerHTML = `<p>Error loading data.</p>`;
        });
}


function hidePopup() {
    popupContainer.style.display = 'none';
    popupContentDiv.innerHTML = '';
}


function onKeyDown(event) {
    switch (event.code) {
        case 'ArrowUp':
        case 'KeyW':
            moveForward = true;
            break;
        case 'ArrowLeft':
        case 'KeyA':
            moveLeft = true;
            break;
        case 'ArrowDown':
        case 'KeyS':
            moveBackward = true;
            break;
        case 'ArrowRight':
        case 'KeyD':
            moveRight = true;
            break;
    }
}

function onKeyUp(event) {
    switch (event.code) {
        case 'ArrowUp':
        case 'KeyW':
            moveForward = false;
            break;
        case 'ArrowLeft':
        case 'KeyA':
            moveLeft = false;
            break;
        case 'ArrowDown':
        case 'KeyS':
            moveBackward = false;
            break;
        case 'ArrowRight':
        case 'KeyD':
            moveRight = false;
            break;
    }
}

// Helper function to determine NPC positions (you can customize these)
function getNpcPosition(npcName) {
    switch (npcName) {
        case 'gymNpc':
            return { x: 5, y: 0, z: 0 };
        case 'learnNpc':
            return { x: -5, y: 0, z: 0 };
        case 'portfolioNpc':
            return { x: 0, y: 0, z: 5 };
        default:
            return { x: 0, y: 0, z: 0 }; // Default position
    }
}
